<span class="line"><span style="color:#B392F0;">    use</span><span style="color:#F97583;">:</span><span style="color:#B392F0;"> [</span><span style="color:#FFAB70;">&quot;style-loader&quot;</span><span style="color:#BBBBBB;">,</span><span style="color:#B392F0;"> </span><span style="color:#FFAB70;">&quot;css-loader?modules&quot;</span><span style="color:#B392F0;">]</span><span style="color:#BBBBBB;">,</span></span>
