<span class="line"><span style="color:#ABB2BF;">  color: </span><span style="color:#56B6C2;">var</span><span style="color:#ABB2BF;">(</span><span style="color:#E06C75;">--lightColor</span><span style="color:#ABB2BF;">);</span></span>
