import{_ as i}from"./chunks/plugin-vue_export-helper.DlAUqK2U.js";import{c as e,o as a,X as r}from"./chunks/vendor-vueuse.XPft87tp.js";const p=JSON.parse('{"title":"面试官：你还有问题要问我吗?","description":"","frontmatter":{},"headers":[],"relativePath":"interview/面试官：你还有问题要问我吗.md","filePath":"interview/面试官：你还有问题要问我吗.md","lastUpdated":1771466668000}'),t={name:"interview/面试官：你还有问题要问我吗.md"};function o(h,l,n,s,u,d){return a(),e("div",null,l[0]||(l[0]=[r('<h1 id="面试官-你还有问题要问我吗" tabindex="-1">面试官：你还有问题要问我吗? <a class="header-anchor" href="#面试官-你还有问题要问我吗" aria-label="Permalink to &quot;面试官：你还有问题要问我吗?&quot;">​</a></h1><p>下面列表里的问题对于参加技术面试的人来说可能有些用。</p><p>列表里的问题并不一定适用于某个特定的职位或者工作类型，也没有排序</p><p>最开始的时候这只是我自己的问题列表，但是慢慢地添加了一些我觉得可能让我对这家公司亮红牌的问题。</p><p>我也注意到被我面试的人提问我的问题太少了，感觉他们挺浪费机会的。</p><h2 id="预期使用方式" tabindex="-1">预期使用方式 <a class="header-anchor" href="#预期使用方式" aria-label="Permalink to &quot;预期使用方式&quot;">​</a></h2><ul><li>检查一下哪些问题你感兴趣</li><li>检查一下哪些是你可以自己在网上找到答案的</li><li>找不到的话就向面试官提问</li></ul><p>绝对不要想把这个列表里的每个问题都问一遍。（尊重面试官的时间，而且你可以通过查找已经发布的答案来显示 你的主动性）</p><p>请记住事情总是灵活的，组织的结构调整也会经常发生。拥有一个 bug 追踪系统并不会保证高效处理 bug。 CI/CD （持续集成系统） 也不一定保证交付时间会很短。</p><h2 id="职责" tabindex="-1">职责 <a class="header-anchor" href="#职责" aria-label="Permalink to &quot;职责&quot;">​</a></h2><ul><li>On-call （电话值班）的计划或者规定是什么？值班或者遇到问题加班时候有加班费吗？</li><li>我的日常工作是什么？</li><li>有给我设定的特定目标吗？</li><li>团队里面初级和高级工程师的比例是多少？（有计划改变吗）</li><li>入职培训 (onboarding) 会是什么样的？</li><li>每个开发者有多大的自由来做出决定？</li><li>在你看来，这个工作做到什么程度算成功？</li><li>你期望我在最初的一个月 / 三个月能够完成什么？</li><li>试用期结束的时候，你会怎么样衡量我的绩效？</li><li>自己单独的开发活动和按部就班工作的比例大概是怎样的？</li><li>一个典型的一天或者一周的工作是怎样安排的？</li><li>对我的申请你有什么疑虑么？</li><li>在这份工作上，我将会和谁紧密合作？</li><li>我的直接上级他们的上级都是什么样的管理风格？（事无巨细还是着眼宏观）</li><li>我在这个岗位上应该如何发展？会有哪些机会？</li><li>每天预期 / 核心工作时间是多少小时？</li><li>我入职的岗位是新增还是接替之前离职的同事？（是否有技术债需要还）？(zh)</li><li>入职之后在哪个项目组，项目是新成立还是已有的？(zh)</li></ul><h2 id="技术" tabindex="-1">技术 <a class="header-anchor" href="#技术" aria-label="Permalink to &quot;技术&quot;">​</a></h2><ul><li>公司常用的技术栈是什么？</li><li>你们怎么使用源码控制系统？</li><li>你们怎么测试代码？</li><li>你们怎么追踪 bug?</li><li>你们怎样监控项目？</li><li>你们怎么集成和部署代码改动？是使用持续集成和持续部署吗 (CI/CD)？</li><li>你们的基础设施搭建在版本管理系统里吗？或者是代码化的吗？</li><li>从计划到完成一项任务的工作流是什么样的？</li><li>你们如何准备故障恢复？</li><li>有标准的开发环境吗？是强制的吗？</li><li>你们需要花费多长时间来给产品搭建一个本地测试环境？（分钟 / 小时 / 天）</li><li>你们需要花费多长时间来响应代码或者依赖中的安全问题？</li><li>所有的开发者都可以使用他们电脑的本地管理员权限吗？</li><li>介绍一下你们的技术原则或者展望。</li><li>你们的代码有开发文档吗？有没有单独的供消费者阅读的文档？</li><li>你们有更高层次的文档吗？比如说 ER 图，数据库范式</li><li>你们使用静态代码分析吗？</li><li>你们如何管理内部和外部的数字资产？</li><li>你们如何管理依赖？</li><li>公司是否有技术分享交流活动？有的话，多久一次呢？(zh)</li><li>你们的数据库是怎么进行版本控制的？(zh)</li><li>业务需求有没有文档记录？是如何记录的？(zh)</li></ul><h2 id="团队" tabindex="-1">团队 <a class="header-anchor" href="#团队" aria-label="Permalink to &quot;团队&quot;">​</a></h2><ul><li>工作是怎么组织的？</li><li>团队内 / 团队间的交流通常是怎样的？</li><li>你们使用什么工具来做项目组织？你的实际体会是什么？</li><li>如果遇到不同的意见怎样处理？</li><li>谁来设定优先级 / 计划？</li><li>如果团队没能赶上预期发布日期怎么办？</li><li>每周都会开什么类型的会议？</li><li>会有定期的和上级的一对一谈话吗？</li><li>产品 / 服务的规划是什么样的？（n 周一发布 / 持续部署 / 多个发布流 / ...)</li><li>生产环境发生事故了怎么办？是否有不批评人而分析问题的文化？</li><li>有没有一些团队正在经历还尚待解决的挑战？</li><li>你们如何跟踪进度？</li><li>预期和目标是如何设定的？谁来设定？</li><li>Code Review 如何实施？</li><li>给我介绍下团队里一个典型的 sprint</li><li>你们如何平衡技术和商业目标？</li><li>你们如何共享知识？</li><li>团队有多大？</li><li>公司技术团队的架构和人员组成？(zh)</li><li>团队内开发、产品、运营哪一方是需求的主要提出方？哪一方更强势？(zh)</li></ul><h2 id="问未来的同事" tabindex="-1">问未来的同事 <a class="header-anchor" href="#问未来的同事" aria-label="Permalink to &quot;问未来的同事&quot;">​</a></h2><ul><li>开发者倾向于从哪里学习？</li><li>你对在这里工作最满意的地方是？</li><li>最不满意的呢？</li><li>如果可以的话，你想改变哪里？</li><li>团队最老的成员在这里多久了？</li><li>在小团队中，有没有出现成员性格互相冲突的情况？最后是如何解决的？</li></ul><h2 id="公司" tabindex="-1">公司 <a class="header-anchor" href="#公司" aria-label="Permalink to &quot;公司&quot;">​</a></h2><ul><li>公司为什么在招人？（产品发展 / 新产品 / 波动...)</li><li>有没有会议 / 旅行预算？使用的规定是什么？</li><li>晋升流程是怎样的？要求 / 预期是怎样沟通的？</li><li>绩效评估流程是怎样的？</li><li>技术和管理两条职业路径是分开的吗？</li><li>对于多元化招聘的现状或者观点是什么？</li><li>有公司级别的学习资源吗？比如电子书订阅或者在线课程？</li><li>有获取证书的预算吗？</li><li>公司的成熟度如何？（早期寻找方向 / 有内容的工作 / 维护中 / ...)</li><li>我可以为开源项目做贡献吗？是否需要审批？</li><li>你认为公司未来五年或者十年会发展成什么样子？</li><li>公司的大多数员工是如何看待整洁代码的？</li><li>你上次注意到有人成长是什么时候？他们在哪方面成长了？</li><li>在这里成功的定义是什么？如何衡量成功？</li><li>有体育活动或者团建么？</li><li>有内部的黑客马拉松活动吗？</li><li>公司支持开源项目吗？</li><li>有竞业限制或者保密协议需要签吗？</li><li>你们认为公司文化中的空白是什么？</li><li>能够跟我说一公司处于不良情况，以及如何处理的故事吗？</li><li>您在这工作了多久了？您觉得体验如何？(zh)</li><li>大家为什么会喜欢这里？(zh)</li><li>公司的调薪制度是如何的？(zh)</li></ul><h2 id="社会问题" tabindex="-1">社会问题 <a class="header-anchor" href="#社会问题" aria-label="Permalink to &quot;社会问题&quot;">​</a></h2><ul><li>你们关于多元化招聘什么看法？</li><li>你们的公司文化如何？你认为有什么空白么？</li><li>这里的工作生活平衡地怎么样？</li><li>公司对气候变化有什么态度吗？</li></ul><h2 id="冲突" tabindex="-1">冲突 <a class="header-anchor" href="#冲突" aria-label="Permalink to &quot;冲突&quot;">​</a></h2><ul><li>不同的意见如何处理？</li><li>如果被退回了会怎样？（“这个在预计的时间内做不完”）</li><li>当团队有压力并且在超负荷工作的时候怎么处理？</li><li>如果有人注意到了在流程或者技术等其他方面又改进的地方，怎么办？</li><li>当管理层的预期和工程师的绩效之间有差距的时候如何处理？</li><li>能给我讲一个公司深处有毒环境以及如何处理的故事吗？</li><li>如果在公司内你的同事因涉嫌性侵犯他人而被调查，请问你会如何处理？</li><li>假设我自己很不幸是在公司内被性侵的受害者，在公司内部有没有争取合法权益的渠道？</li></ul><h2 id="商业" tabindex="-1">商业 <a class="header-anchor" href="#商业" aria-label="Permalink to &quot;商业&quot;">​</a></h2><ul><li>你们现在盈利吗？</li><li>如果没有的话，还需要多久？</li><li>公司的资金来源是什么？谁影响或者制定高层计划或方向？</li><li>你们如何挣钱？</li><li>什么阻止了你们挣更多的钱？</li><li>公司未来一年的增长计划怎样？五年呢？</li><li>你们认为什么是你们的竞争优势？</li><li>你们的竞争优势是什么？</li><li>公司未来的商业规划是怎样的？有上市的计划吗？(zh)</li></ul><h2 id="远程工作" tabindex="-1">远程工作 <a class="header-anchor" href="#远程工作" aria-label="Permalink to &quot;远程工作&quot;">​</a></h2><ul><li>远程工作和办公室工作的比例是多少？</li><li>公司提供硬件吗？更新计划如何？</li><li>使用自己的硬件办公可以吗？现在有政策吗？</li><li>额外的附件和家具可以通过公司购买吗？这方面是否有预算？</li><li>有共享办公或者上网的预算吗？</li><li>多久需要去一次办公室？</li><li>公司的会议室是否一直是视频会议就绪的？</li></ul><h2 id="办公室布局" tabindex="-1">办公室布局 <a class="header-anchor" href="#办公室布局" aria-label="Permalink to &quot;办公室布局&quot;">​</a></h2><ul><li>办公室的布局如何？（开放的 / 小隔间 / 独立办公室）</li><li>有没有支持 / 市场 / 或者其他需要大量打电话的团队在我的团队旁边办公？</li></ul><h2 id="终极问题" tabindex="-1">终极问题 <a class="header-anchor" href="#终极问题" aria-label="Permalink to &quot;终极问题&quot;">​</a></h2><ul><li>该职位为何会空缺？</li><li>公司如何保证人才不流失？</li><li>这份工作 / 团队 / 公司最好和最坏的方面是？</li><li>你最开始为什么选择了这家公司？</li><li>你为什么留在这家公司？</li></ul><h2 id="待遇" tabindex="-1">待遇 <a class="header-anchor" href="#待遇" aria-label="Permalink to &quot;待遇&quot;">​</a></h2><ul><li>如果有奖金计划的话，奖金如何分配？</li><li>如果有奖金计划的话，过去的几年里通常会发百分之多少的奖金？</li><li>有五险一金(zh)/401k(us)或者其他退休养老金等福利吗？</li><li>五险一金中，补充公积金一般交多少比例？/401k一般交多少比例？我可以自己选择这一比例吗？</li><li>有什么医疗保险吗？如果有的话何时开始？</li><li>有额外商业保险吗？例如人寿保险和额外的养老/医疗保险？</li><li>更换工作地点，公司付费吗？</li></ul><h2 id="休假" tabindex="-1">休假 <a class="header-anchor" href="#休假" aria-label="Permalink to &quot;休假&quot;">​</a></h2><ul><li>带薪休假时间有多久？</li><li>病假和事假是分开的还是一起算？</li><li>我可以提前使用假期时间吗？也就是说应休假期是负的？</li><li>假期的更新策略是什么样的？也就是说未休的假期能否滚入下一周期</li><li>照顾小孩的政策如何？</li><li>无薪休假政策是什么样的？</li><li>学术性休假政策是怎么样的？</li></ul><h2 id="参考链接" tabindex="-1">参考链接 <a class="header-anchor" href="#参考链接" aria-label="Permalink to &quot;参考链接&quot;">​</a></h2><p>Find more inspiration for questions in:</p><ul><li><a href="https://www.joelonsoftware.com/2000/08/09/the-joel-test-12-steps-to-better-code/" target="_blank" rel="noreferrer">The Joel Test: 12 Steps to Better Code</a> by Joel Spolsky</li><li><a href="https://jvns.ca/blog/2013/12/30/questions-im-asking-in-interviews/" target="_blank" rel="noreferrer">Questions I&#39;m asking in interviews</a> by Julia Evans</li></ul><blockquote><p><a href="https://github.com/viraptor/reverse-interview" target="_blank" rel="noreferrer">https://github.com/viraptor/reverse-interview</a></p><p>翻译：</p><p><a href="https://github.com/viraptor/reverse-interview" target="_blank" rel="noreferrer">English</a></p><p><a href="https://github.com/JaeYeopHan/Interview_Question_for_Beginner/blob/master/Reverse_Interview/README.md" target="_blank" rel="noreferrer">Korean</a></p><p><a href="https://github.com/viraptor/reverse-interview/blob/master/translations/PORTUGUESE.md" target="_blank" rel="noreferrer">Portuguese</a></p><p><a href="https://github.com/NeroCube/reverse-interview-zh-tw/blob/master/README.md" target="_blank" rel="noreferrer">繁體中文</a></p></blockquote>',39)]))}const _=i(t,[["render",o]]);export{p as __pageData,_ as default};
